PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer not null primary key autoincrement, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer not null primary key autoincrement, "name" varchar not null, "email" varchar not null, "password" varchar not null, "roles" text not null, "remember_token" varchar null, "created_at" datetime null, "updated_at" datetime null);
CREATE TABLE IF NOT EXISTS "password_resets" ("email" varchar not null, "token" varchar not null, "created_at" datetime null);
CREATE TABLE IF NOT EXISTS "contacts" ("id" integer not null primary key autoincrement, "name" varchar not null, "email" varchar not null, "subject" varchar not null, "phone" varchar null, "user_id" integer not null default '0', "message" text not null, "created_at" datetime null, "updated_at" datetime null);
CREATE TABLE IF NOT EXISTS "assets" ("id" integer not null primary key autoincrement, "barcode" varchar not null, "serial" varchar not null, "name" varchar not null, "description" text not null, "brand" varchar null, "model" varchar null, "warranty" integer null, "condition" varchar not null, "life_time" integer not null, "depreciation_rate" integer not null, "area_id" integer not null, "branch_id" integer not null, "classification_id" integer not null, "supplier_id" integer not null, "section_id" integer not null, "user_id" integer not null, "status" tinyint(1) not null, "purchase_date" date not null, "purchase_price" integer not null, "receipt_code" integer null, "receipt_date" date not null, "maintenance_date" date null, "image" varchar null, "created_at" datetime null, "updated_at" datetime null, foreign key("supplier_id") references "suppliers"("id") on delete cascade, foreign key("classification_id") references "classifications"("id") on delete cascade, foreign key("section_id") references "sections"("id") on delete cascade, foreign key("area_id") references "areas"("id") on delete cascade, foreign key("branch_id") references "branches"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "settings" ("id" integer not null primary key autoincrement, "set_slug" varchar not null, "set_name" varchar not null, "value" text not null, "type" integer not null default '1');
CREATE TABLE IF NOT EXISTS "suppliers" ("id" integer not null primary key autoincrement, "supplier_name" varchar not null, "contact" integer not null, "address" text not null);
CREATE TABLE IF NOT EXISTS "areas" ("id" integer not null primary key autoincrement, "area_name" varchar not null, "value" text null);
CREATE TABLE IF NOT EXISTS "sections" ("id" integer not null primary key autoincrement, "section_name" varchar not null, "branch_id" integer not null, "value" text null, foreign key("branch_id") references "branches"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "classifications" ("id" integer not null primary key autoincrement, "class_name" varchar not null, "section_id" integer not null, "value" text null, "created_at" datetime null, "updated_at" datetime null, foreign key("section_id") references "sections"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "branches" ("id" integer not null primary key autoincrement, "branch_name" varchar not null, "area_id" integer not null, "value" text null, foreign key("area_id") references "areas"("id") on delete cascade);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "password_resets_email_index" on "password_resets" ("email");
CREATE UNIQUE INDEX "assets_barcode_unique" on "assets" ("barcode");
COMMIT;
